struct Solution{}
impl Solution {
    pub fn longest_palindrome(s: String) -> String {
        let chars: Vec<char> = s.chars().collect();
        let n = chars.len();
        let mut ans = String::from("");
        let mut arr = Self::prepare_array(&chars);
        for i in 2..chars.len() {
            for j in 0..(n - i) {
                if chars[j] == chars[i+j] {
                    if arr[j+1][i + j -1] {
                        arr[j][i+j] = true;
                    }
                }
            }
        }
        let mut start = 0;
        let mut end = 0;
        for i in 0..n {
            for j in i..n {
                if arr[i][j] && j - i > end - start {
                    start = i;
                    end = j;
                }
            }
        }
        ans = s[start..end+1].to_string();
        ans
    }
    pub fn prepare_array(s: &Vec<char>) -> Vec<Vec<bool>> {
        let n = s.len();
        let mut arr = vec![vec![false; n]; n];
        for i in 0..n {
            arr[i][i] = true;
        }
        for i in 0..n-1 {
            if s[i] == s[i+1] {
                arr[i][i+1] = true;
            }
        }
        arr
    }
}

#[cfg(test)]
mod tests {
    use crate::leetcode::p00005::Solution;

    #[test]
    fn test1() {
        let ans = Solution::longest_palindrome(String::from("ababa"));
        assert_eq!("ababa", ans);
    }

    #[test]
    fn test2() {
        let ans = Solution::longest_palindrome(String::from("cbbd"));
        assert_eq!("bb", ans);
    }

    #[test]
    fn test3() {
        let ans = Solution::longest_palindrome(String::from(""));
        assert_eq!("", ans);
    }
}